import React, { Component } from 'react';
import './App.css';


class Chronometre extends Component {
  constructor(){
    super();
    this.state = {
      totalSeconds :0,
      status: 'unstarted',
      tick: 0,
    };
  }

  startChrono = () => {
    console.log('start');

    this.setState({
      status: 'started',
    });
    console.log(this.state);
  }

  stopChrono = () => {
    this.setState({
      status: 'stopped',
    });
    console.log('stop');
    console.log(this.state);
  }

  resetChrono = () => {
    this.setState({
      totalSeconds: 0,
      status: 'unstarted',
    });
    console.log('reset');
    console.log(this.state);
  }



  render(){
    if (this.state.status === 'started'){
      var timer = setInterval(() => {
        this.setState({
          totalSeconds: this.state.totalSeconds + 1,
        });
      }, 1000);
    }
    if (this.state.status === 'stopped'){
      clearInterval(timer);
    }

    return(
      <div className="Chronometre">
        <h1>Chronometre</h1>
        <div className="Chronometre-display">
          <span>{this.state.totalSeconds}</span>
        </div>
        <div className="Chronometre-controls">
          <button onClick={this.startChrono}>Start</button>
          <button onClick={this.stopChrono}>Stop</button>
          <button onClick={this.resetChrono}>Reset</button>
        </div>
      </div>
    )
  }
}

export default Chronometre;
