import logo from './logo.svg';
import './App.css';


class Chronometre extends componant {
  constructor(){
    super();
    this.state = {
      totalSeconds :0,
      status: 'unstarted',
    };
  }
  render(){
    return(
      <div className="Chronometre">
        <h1>Chronometre</h1>
        <div className="Chronometre-display">
          <span>{this.state.totalSeconds}</span>
        </div>
        <div className="Chronometre-controls">
          <button>Start</button>
          <button>Stop</button>
          <button>Reset</button>
        </div>
      </div>
    )
  }
}


function App() {
  return (
    <div className="App">
      <header className="Chronometre">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default Chronometre;
